
def get_header(auth):
    headers = auth.login('student', 'Test@123')
    return headers

def test_student_profile_success(client,auth):
    headers = get_header(auth)

    data = {
    "first_name": "student",
    "last_name": "student",
    "email": "student@gmail.com",
    "username": "student",
    "phone_number": "9871565871",
    "address" : "Mumbai",
    "date_of_birth" : "1999-01-01",
    "gender" : "M",
    "class_taken" : 1,
    # "profile": "data:image/jpeg;base64,;"
}

    response = client.put('/student/profile', headers=headers, json=data)

    assert response.status_code == 200
    assert response.json == {'message': 'User updated successfully'}

def test_get_subject_teachers(client, auth):
    headers = get_header(auth)

    response = client.get('/student/subject_teachers', headers=headers)
    response_json = response.get_json()

    assert response.status_code == 200
    assert 'teacher_list' in response_json

def test_get_marks(client, auth):
    headers = get_header(auth)   

    response = client.get('/student/get_marks', headers=headers)
    response_json = response.get_json()

    assert response.status_code == 200
    assert 'data' in response_json